import React, { useRef, useState } from 'react';
import html2pdf from 'html2pdf.js';

function App() {
  const formRef = useRef();

  // State to store form data
  const [formData, setFormData] = useState({
    regno: '',
    date: '',
    receivedFrom: '',
    phone: '',
    email: '',
    paymentDetails: '',
    purpose: '',
    amount: '',
    mode: '',
    transactionId: '',
    agree: false
  });

  // Update state when inputs change
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Ensure images are loaded before downloading PDF
  const loadImagesThenDownload = () => {
    const images = formRef.current.querySelectorAll("img");
    const promises = Array.from(images).map(img => {
      if (img.complete) return Promise.resolve();
      return new Promise(resolve => {
        img.onload = img.onerror = resolve;
      });
    });
    return Promise.all(promises);
  };

  // Generate PDF with user data
  const handleSubmit = async (e) => {
    e.preventDefault();
    await loadImagesThenDownload();

    const opt = {
      margin: 0.5,
      filename: 'akasa_receipt.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'in', format: 'letter', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(formRef.current).save();
  };

  const headerStyle = {
    backgroundColor: '#f0f0f0',
    padding: '20px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  };

  const titleStyle = {
    margin: 0,
    fontSize: '24px',
    textAlign: 'center',
  };

  const logoStyle = {
    height: '60px',
    marginRight: '20px',
  };

  const formWrapperStyle = {
    display: 'flex',
    justifyContent: 'center',
    padding: '40px 20px',
    backgroundColor: '#f9f9f9',
  };

  const formContainerStyle = {
    width: '100%',
    maxWidth: '600px',
    padding: '30px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    fontFamily: 'Arial, sans-serif',
  };

  const formRowStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  };

  const labelStyle = {
    width: '150px',
    fontWeight: 'bold',
  };

  const inputStyle = {
    flex: 1,
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  };

  const submitStyle = {
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '4px',
    marginTop: '20px',
  };

  return (
    <div ref={formRef}>
      {/* Header */}
      <div style={headerStyle}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src="https://akasainstitute.com/wp-content/uploads/2025/05/WhatsApp-Image-2025-04-25-at-9.31.43-PM.jpeg"
            alt="Logo"
            style={logoStyle}
          />
          <div>
            <h1 style={titleStyle}>Akasa Institute</h1>
            <p style={{ margin: 0 }}>(A unit of Akasa Unitech PvtLtd)</p>
          </div>
        </div>
      </div>

      {/* Form Wrapper */}
      <div style={formWrapperStyle}>
        <form style={formContainerStyle} onSubmit={handleSubmit}>
          {[
            ['Register No:', 'regno', 'number'],
            ['Date:', 'date', 'date'],
            ['Received from:', 'receivedFrom', 'text'],
            ['Contact Us:', 'phone', 'tel'],
            ['Email Id:', 'email', 'email'],
            ['Payment Details:', 'paymentDetails', 'text'],
            ['Purpose:', 'purpose', 'text'],
            ['Amount:', 'amount', 'number'],
            ['Transaction ID:', 'transactionId', 'text'],
          ].map(([label, name, type]) => (
            <div style={formRowStyle} key={name}>
              <label style={labelStyle}>{label}</label>
              <input
                type={type}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                style={inputStyle}
                required
              />
            </div>
          ))}

          {/* Payment Mode */}
          <div style={formRowStyle}>
            <label style={labelStyle}>Payment Mode:</label>
            <select name="mode" value={formData.mode} onChange={handleChange} style={inputStyle} required>
              <option value="">---Select---</option>
              <option value="upi">UPI</option>
              <option value="cash">Cash</option>
              <option value="bank">Bank Transaction</option>
            </select>
          </div>

          {/* Agreement */}
          <div style={formRowStyle}>
            <label style={labelStyle}></label>
            <label>
              <input
                type="checkbox"
                name="agree"
                checked={formData.agree}
                onChange={handleChange}
                required
              />{' '}
              I confirm the information is correct.
            </label>
          </div>

      {/* Header */}
      <div style={headerStyle}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src="https://akasainstitute.com.jpeg"
            alt="Logo"
            style={logoStyle}
          />
        </div>
      </div>



          {/* Submit Button */}
          <div style={formRowStyle}>
            <label style={labelStyle}></label>
            <button type="submit" style={submitStyle}>Submit & Download PDF</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
