import React, { useEffect, useState } from 'react';

const Trash = () => {
  const [trashedApps, setTrashedApps] = useState([]);

  const fetchTrash = async () => {
    const res = await fetch('http://localhost:5000/workshop');
    const data = await res.json();
    setTrashedApps(data);
  };

const handleRestore = async (id) => {
  await fetch(`http://localhost:5000/workshop/restore${id}`, {
    method: 'POST',
  });
  fetchTrash(); // refresh
};


  const handlePermanentDelete = async (id) => {
    if (window.confirm('Permanently delete this application?')) {
      await fetch(`http://localhost:5000/Trash/delete/:id/${id}`, { method: 'DELETE' });
      fetchTrash(); // refresh
    }
  };

  useEffect(() => {
    fetchTrash();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Trash</h2>
      {trashedApps.length === 0 ? (
        <p>No deleted applications.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {trashedApps.map(app => (
              <tr key={app.id}>
                <td>{app.firstName} {app.lastName}</td>
                <td>{app.phone}</td>
                <td>{app.email}</td>
                <td>
                  <button onClick={() => alert(JSON.stringify(app, null, 2))}>View</button>
                  <button onClick={() => handleRestore(app.id)} style={{ marginLeft: '10px', backgroundColor: 'green', color: '#fff' }}>Restore</button>
                  <button onClick={() => handlePermanentDelete(app.id)} style={{ marginLeft: '10px', backgroundColor: 'red', color: '#fff' }}>Delete Permanently</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Trash;
