import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ViewApplication from './ViewApplication';

const AdminPanel = () => {
  const [applications, setApplications] = useState([]);
  const [message, setMessage] = useState('');
  const [selectedApplication, setSelectedApplication] = useState(null);
  const API_ENDPOINT = 'http://localhost:5000/workshop';

  const fetchApplications = async () => {
    try {
      const response = await fetch(API_ENDPOINT);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setApplications(data);
    } catch (error) {
      console.error('Error fetching applications:', error);
      setMessage('Error fetching applications');
    }
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this application?')) {
      try {
        const response = await fetch(`${API_ENDPOINT}/${id}`, {
          method: 'DELETE',
        });
        const data = await response.json();
        if (response.ok) {
          setMessage(data.message);
          setApplications(applications.filter(application => application.id !== id));
        } else {
          setMessage('Error deleting application');
        }
      } catch (error) {
        console.error('Error deleting application:', error);
        setMessage('Error deleting application');
      }
    }
  };

  const handleView = (application) => {
    setSelectedApplication(application);
  };

  const handleCloseView = () => {
    setSelectedApplication(null);
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      {message && <p>{message}</p>}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Gender</th>
            <th>Mode</th>
            <th>Are You A</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {applications.map(application => (
            <tr key={application.id}>
              <td>{application.firstName} {application.lastName}</td>
              <td>{application.phone}</td>
              <td>{application.email}</td>
              <td>{application.gender}</td>
              <td>{application.mode}</td>
              <td>{application.areYouA}</td>
              <td>
                <button onClick={() => handleView(application)}>View</button>
                <Link to={`/edit/${application.id}`}>Edit</Link>
                <button onClick={() => handleDelete(application.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedApplication && (
        <ViewApplication application={selectedApplication} onClose={handleCloseView} />
      )}
    </div>
  );
};

export default AdminPanel;
