import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import 'react-phone-input-2/lib/style.css';
import PhoneInput from 'react-phone-input-2';

const EditApplication = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    gender: '',
    mode: '',
    areYouA: '',
    aitool: '',
    current: '',
    chooseworkshop: '',
    aboutas: '',
    preferredBatch: ''
  });

  const [errors, setErrors] = useState({
    email: '',
    phone: '',
  });

  useEffect(() => {
    const fetchApplication = async () => {
      try {
        const response = await fetch(`http://localhost:5000/workshop/${id}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setFormData(data);
      } catch (error) {
        console.error('Error fetching application:', error);
      }
    };

    fetchApplication();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validate email and phone on change
    if (name === 'email') {
      if (value && !validateEmail(value)) {
        setErrors(prevErrors => ({ ...prevErrors, email: 'Invalid email format' }));
      } else {
        setErrors(prevErrors => ({ ...prevErrors, email: '' }));
      }
    }

    if (name === 'phone') {
      if (value && !validatePhone(value)) {
        setErrors(prevErrors => ({ ...prevErrors, phone: 'Invalid phone format (10 digits)' }));
      } else {
        setErrors(prevErrors => ({ ...prevErrors, phone: '' }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (errors.email || errors.phone) {
      alert("Please correct the errors in the form.");
      return;
    }
    try {
      const response = await fetch(`http://localhost:5000/workshop/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Application updated successfully!');
        navigate('/AdminPanel');
      } else {
        alert('Error updating application.');
      }
    } catch (error) {
      console.error('Error updating application:', error);
      alert('Error updating application.');
    }
  };

  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const validatePhone = (phone) => {
    const regex = /^\d{10}$/; // Validates that the phone number is exactly 10 digits
    return regex.test(phone);
  };

  return (
    <div className="edit-container">
      <h2>Edit Application</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
        </div>
        <div>
          <label>Phone Number:</label>
          <PhoneInput
            country={'in'}
            value={formData.phone}
            onChange={(value) => setFormData(prev => ({ ...prev, phone: value }))}
          />
        </div>
        <div>
          <label>Email:</label>
          <input type="text" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Gender:</label>
          <select name="gender" value={formData.gender} onChange={handleChange}>
            <option value="">-- Select Gender --</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div>
          <label>Mode:</label>
          <select name="mode" value={formData.mode} onChange={handleChange}>
            <option value="">-- Select Mode --</option>
            <option value="morning">Morning</option>
            <option value="evening">Evening</option>
          </select>
        </div>
        <div>
          <label>Are You a:</label>
          <select name="areYouA" value={formData.areYouA} onChange={handleChange}>
            <option value="">-- Select Course --</option>
            <option value="student">Student</option>
            <option value="workprofession">Work Profession</option>
            <option value="entrepreneur">Entrepreneur</option>
            <option value="freelancer">Freelancer</option>
            <option value="other">Other</option>
          </select>
        </div>
        <div>
          <label>Have You Used AI Tools:</label>
          <select name="aitool" value={formData.aitool} onChange={handleChange}>
            <option value="">-- Select Mode --</option>
            <option value="yes">Yes</option>
            <option value="no">No</option>
            <option value="Heard">Heard Them But Not Used</option>
          </select>
        </div>
        <div>
          <label>Your Current Field / Industry:</label>
          <input type="text" name="current" value={formData.current} onChange={handleChange} />
        </div>
        <div>
          <label>Why Choosing This Workshop?:</label>
          <input type="text" name="chooseworkshop" value={formData.chooseworkshop} onChange={handleChange} />
        </div>
        <div>
          <label>How Did You Hear About Us? :</label>
          <select name="aboutas" value={formData.aboutas} onChange={handleChange}>
            <option value="">Select</option>
            <option value="facebook/instagram">facebook/instagram</option>
            <option value="googlesearch">googlesearch</option>
            <option value="newspaper">newspaper</option>
            <option value="family">family</option>
            <option value="akasastudent">Akasa Student</option>
            <option value="other">Other</option>
          </select>
        </div>
        <div>
          <label>Preferred Batch:</label>
          <select name="preferredBatch" value={formData.preferredBatch} onChange={handleChange}>
            <option value="">Select</option>
            <option value="june">June 13, 2025 - Offline only at Akasa Institute EkKatuthangal Chennai</option>
