// Akasaworkshop.js
import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import 'react-phone-input-2/lib/style.css';
import PhoneInput from 'react-phone-input-2';

const Akasaworkshop = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    gender: '',
    mode: '',
    areYouA: '',
    aitool: '',
    current: '',
    chooseworkshop: '',
    aboutas: '',
    preferredBatch: '',
  });

  const [isValid, setIsValid] = useState(true);
  const [errors, setErrors] = useState({
    email: '',
    phone: '',
  });

  const goToAdminLoginn = () => {
    navigate('/AdminLoginn');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.firstName || !formData.lastName || !formData.phone || !formData.email || !formData.areYouA || !formData.aitool || !formData.current || !formData.chooseworkshop || !formData.aboutas || !formData.preferredBatch) {
      alert("Please fill in all required fields.");
      return;
    }
    if (errors.email || errors.phone) {
      alert("Please correct the errors in the form.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/workshop", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok) {
        alert(result.message || "Data saved successfully");
        setFormData({
          firstName: '',
          lastName: '',
          phone: '',
          email: '',
          gender: '',
          mode: '',
          areYouA: '',
          aitool: '',
          current: '',
          chooseworkshop: '',
          aboutas: '',
          preferredBatch: ''
        });
      } else {
        alert(result.error || "Failed to save data");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while saving data");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData(prev => ({ ...prev, [name]: value }));

    if (name === 'email') {
      if (value && !validateEmail(value)) {
        setErrors(prevErrors => ({ ...prevErrors, email: 'Invalid email format' }));
      } else {
        setErrors(prevErrors => ({ ...prevErrors, email: '' }));
      }
    }

    if (name === 'phone') {
      if (value && !validatePhone(value)) {
        setErrors(prevErrors => ({ ...prevErrors, phone: 'Invalid phone format (10 digits)' }));
      } else {
        setErrors(prevErrors => ({ ...prevErrors, phone: '' }));
      }
    }
  };

  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  const validatePhone = (phone) => {
    const regex = /^\d{10}$/;
    return regex.test(phone);
  };

  const headerStyle = {
    position: 'relative',
    width: '100%',
    padding: '20px 40px',
    color: 'white',
    borderRadius: '0 0 12px 12px',
    textAlign: 'center',
    boxSizing: 'border-box',
    background: 'linear-gradient(to right, #ffffff, #f1f1f1)',
  };

  const logoStyle = {
    position: 'absolute',
    left: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
    width: '120px',
    height: '100px',
    objectFit: 'contain',
  };

  const adminStyle = {
    position: 'absolute',
    right: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
    width: '60px',
    height: '60px',
    objectFit: 'contain',
    cursor: 'pointer',
  };

  const titleStyle = {
    fontSize: '28px',
    fontWeight: '700',
    margin: 0,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: 'black',
  };

  const styles = {
    submitButton: {
      padding: '12px',
      backgroundColor: '#0077cc',
      color: 'white',
      border: 'none',
      borderRadius: '6px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      marginTop: '15px',
    }
  };

  return (
    <>
      <style>
        {`
          .form-container {
            display: flex;
            flex-direction: column;
            gap: 15px;
            padding: 25px;
            max-width: 600px;
            margin: 0 auto 40px;
            background: linear-gradient(to right, #ffffff, #f1f1f1);
            border-radius: 12px;
            box-shadow: 0 6px 18px rgba(0, 0, 0, 0.1);
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: #333;
            padding-top: 20px;
          }

          .form-row {
            display: flex;
            flex-direction: column;
          }

          .form-row label {
            font-weight: 600;
            margin-bottom: 6px;
            color: #222;
          }

          .form-row input,
          .form-row select {
            padding: 10px;
            border-radius: 6px;
            border: 1px solid #ccc;
            font-size: 15px;
            transition: 0.3s ease;
          }

          .form-row input:focus,
          .form-row select:focus {
            outline: none;
            border-color: #0077cc;
            box-shadow: 0 0 0 3px rgba(0, 119, 204, 0.1);
          }

          .form-row-two {
            display: flex;
            gap: 20px;
          }

          .form-row-two input {
            flex: 1;
          }

          .error-text {
            color: red;
            margin-top: 5px;
            font-size: 13px;
          }

          .required {
            color: red;
            margin-left: 4px;
          }

          button:hover {
            background-color: #005fa3;
          }
        `}
      </style>

      <div style={headerStyle}>
        <header>
          <h1 style={titleStyle}>Akasa Institute Workshop</h1>
          <img
            src="https://akasainstitute.com/wp-content/uploads/2025/05/WhatsApp-Image-2025-04-25-at-9.31.43-PM.jpeg"
            alt="Logo"
            style={logoStyle}
          />
          <button style={adminStyle} onClick={goToAdminLoginn}>Admin</button>
        </header>
      </div>

      <div style={{ paddingTop: '5%' }}>
        <form className="form-container" onSubmit={handleSubmit}>
          <h3 style={{ textAlign: 'center' }}>Register Upcoming Workshop</h3>

          {/* Name */}
          <div className="form-row">
            <div className="form-row-two">
              <div className="form-group">
                <label htmlFor="firstName">
                  First Name <span className="required">*</span>
                </label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  placeholder="First Name"
                  value={formData.firstName}
                  onChange={handleChange}
                  required
                  style={{ width: '90%'}}
                />
              </div>

              <div className="form-group">
                <label htmlFor="lastName">Last Name <span className="required">*</span>
                </label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  placeholder="Last Name"
                  value={formData.lastName}
                  onChange={handleChange}
                  required
                  style={{ width: '90%'}}
                />
              </div>
            </div>
          </div>

          {/* Phone Numbers and Email */}
          <div className="form-row">
            <div className="form-row-two">
              <div style={{ flex: 1 }}>
                <label>Phone Number <span className="required">*</span></label>
                <PhoneInput
                  country={'in'}
                  value={formData.phone}
                  onChange={(value) => setFormData(prev => ({ ...prev, phone: value }))}
                  enableSearch={true}
                  inputStyle={{ width: '100%' }}
                />
                {!isValid && (
                  <div style={{ color: 'red', marginTop: '5px' }}>
                    Please enter a valid 10-digit Indian phone number.
                  </div>
                )}
              </div>

              <div style={{ flex: 1 }}>
                <label>Email <span className="required">*</span></label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  style={{ width: '100%' }}
                />
                {errors.email && <div className="error-text">{errors.email}</div>}
              </div>
            </div>
          </div>

          {/* Gender and Mode */}
          <div className="form-row">
            <div className="form-row-two">
              <div style={{ flex: 1 }}>
                <label>Gender <span className="required">*</span></label>
                <select
                  name="gender"
                  value={formData.gender}
                  onChange={handleChange}
                  required
                  style={{ width: '100%' }}
                >
                  <option value="">Select</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div style={{ flex: 1 }}>
                <label>Mode<span className="required">*</span></label>
                <select
                  name="mode"
                  value={formData.mode}
                  onChange={handleChange}
                  required
                  style={{ width: '100%' }}
                >
                  <option value="">Select</option>
                  <option value="online">Online</option>
                  <option value="offline">Offline</option>
                </select>
              </div>
            </div>
          </div>

          {/* Are You a and Have You Used AI Tools */}
          <div className="form-row">
            <div className="form-row-two">
              <div style={{ flex: 1 }}>
                <label>Are You a <span className="required">*</span></label>
                <select
                  name="areYouA"
                  value={formData.areYouA}
                  onChange={handleChange}
                  required
                  style={{ width: '100%' }}
                >
                  <option value="">Select</option>
                  <option value="student">Student</option>
                  <option value="workprofession">Work Profession</option>
                  <option value="entrepreneur">Entrepreneur</option>
                  <option value="freelancer">Freelancer</option>
                  <option value="other">Other</option>
                </select>
              </div>

              <div style={{ flex: 1 }}>
                <label>Have You Used AI Tools <span className="required">*</span></label>
                <select
                  name="aitool"
                  value={formData.aitool}
                  onChange={handleChange}
                  required
                  style={{ width: '100%' }}
                >
                  <option value="">Select course</option>
                  <option value="yes">Yes</option>
                  <option value="no">No</option>
                  <option value="Heard">Heard Them But Not Used</option>
                                </select>
                            </div>
                        </div>
                    </div>

                    {/* Current Field / Industry and Why Choosing This Workshop */}
                    <div className="form-row">
                        <div className="form-row-two">
                            <div style={{ flex: 1 }}>
                                <label>Your Current Field / Industry<span className="required">*</span></label>
                                <input
                                    type='text'
                                    name="current"
                                    value={formData.current}
                                    onChange={handleChange}
                                    required
                                    style={{ width: '90%' }} // Full width
                                />
                            </div>

                            <div style={{ flex: 1 }}>
                                <label>Why Choosing This Workshop?<span className="required">*</span></label>
                                <input
                                    type="text"
                                    name="chooseworkshop"
                                    value={formData.chooseworkshop}
                                    onChange={handleChange}
                                    required
                                    style={{ width: '90%' }} // Full width
                                />
                            </div>
                        </div>
                    </div>

                    {/* How Did You Hear About Us? and Preferred Batch */}
                    <div className="form-row">
                        <div className="form-row-two">
                            <div style={{ flex: 1 }}>
                                <label>How Did You Hear About Us? <span className="required">*</span></label>
                                <select
                                    name="aboutas"
                                    value={formData.aboutas}
                                    onChange={handleChange}
                                    required
                                    style={{ width: '100%' }} // Full width
                                >
                                    <option value="">Select</option>
                                    <option value="facebook/instagram">facebook/instagram</option>
                                    <option value="googlesearch">googlesearch</option>
                                    <option value="newspaper">newspaper</option>
                                    <option value="family">family</option>
                                    <option value="akasastudent">Akasa Student</option>
                                    <option value="other">Other</option>
                                </select>
                            </div>

                            <div style={{ flex: 1 }}>
                                <label>Preferred Batch <span className="required">*</span></label>
                                <select
                                    name="preferredBatch"
                                    value={formData.preferredBatch}
                                    onChange={handleChange}
                                    required
                                    style={{ width: '100%' }} // Full width
                                >
                                    <option value="">Select</option>
                                    <option value="june">June 13, 2025 - Offline only at Akasa Institute EkKatuthangal Chennai</option>
                                    <option value="june28">June 28, 2025 - Online & Offline option available</option>
                                </select>
                            </div>
                        </div>
                    </div>
{/* Consent & Confirmation */}
<div className="form-row">
  <label style={{ color: 'black' }}>
    Consent & Confirmation <span className="required">*</span>
  </label>
  <p style={{ margin: '5px 0 10px' }}>
    I agree to be contacted via WhatsApp / Email for important updates regarding the workshop.
  </p>
  <div style={{ display: 'flex', alignItems: 'center', marginBottom: '1rem' }}>
    <input
      type="checkbox"
      name="contact_agree"
      required
      style={{ width: '16px', height: '16px', marginRight: '8px' }}
    />
    <span>I give my consent to receive communications.</span>
  </div>
</div>
<button type="submit" style={styles.submitButton}>Submit</button>

                </form>
            </div>
        </>
    );
};

export default Akasaworkshop;
