import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import 'react-phone-input-2/lib/style.css';
import PhoneInput from 'react-phone-input-2';

const Edit = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        email: '',
        gender: '',
        mode: '',
        areYouA: '',
        aitool: '',
        current: '',
        chooseworkshop: '',
        aboutas: '',
        preferredBatch: ''
    });

    const [errors, setErrors] = useState({
        email: '',
        phone: '',
    });

    useEffect(() => {
        const fetchApplication = async () => {
            try {
                const response = await fetch(`http://localhost:5001/workshop/${id}`);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setFormData(data);
            } catch (error) {
                console.error('Error fetching application:', error);
            }
        };

        fetchApplication();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });

        // Validate email and phone on change
        if (name === 'email') {
            if (value && !validateEmail(value)) {
                setErrors(prevErrors => ({ ...prevErrors, email: 'Invalid email format' }));
            } else {
                setErrors(prevErrors => ({ ...prevErrors, email: '' }));
            }
        }

        if (name === 'phone') {
            if (value && !validatePhone(value)) {
                setErrors(prevErrors => ({ ...prevErrors, phone: 'Invalid phone format (10 digits)' }));
            } else {
                setErrors(prevErrors => ({ ...prevErrors, phone: '' }));
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (errors.email || errors.phone) {
            alert("Please correct the errors in the form.");
            return;
        }
        try {
            console.log('Submitting form data:', formData); // Log form data
            const response = await fetch(`http://localhost:5001/workshop/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                alert('Application updated successfully!');
                navigate('/AdminPanel');
            } else {
                const errorData = await response.json();
                alert(`Error updating application: ${errorData.message || 'Unknown error'}`);
            }
        } catch (error) {
            console.error('Error updating application:', error);
            alert('Error updating application. Please try again later.');
        }
    };

    const validateEmail = (email) => {
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return regex.test(email);
    };

    const validatePhone = (phone) => {
        const regex = /^\d{10}$/; // Validates that the phone number is exactly 10 digits
        return regex.test(phone);
    };

    const containerStyle = {
        maxWidth: '800px',
        margin: '20px auto',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    };

    const headingStyle = {
        textAlign: 'center',
        color: '#333',
    };

    const formStyle = {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    };

    const formGroupStyle = {
        width: '48%',
        marginBottom: '15px',
    };

    const labelStyle = {
        display: 'block',
        marginBottom: '5px',
        fontWeight: 'bold',
        color: '#555',
    };

    const inputStyle = {
        width: '100%',
        padding: '8px',
        borderRadius: '4px',
        border: '1px solid #ccc',
        fontSize: '16px',
    };

    const submitButtonStyle = {
        backgroundColor: '#4CAF50',
        color: 'white',
        padding: '10px 15px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '16px',
        width: '100%',
    };

    return (
        <div className="edit-container" style={containerStyle}>
            <h2 style={headingStyle}>Edit Application</h2>
            <form onSubmit={handleSubmit} className="edit-form" style={formStyle}>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="firstName" style={labelStyle}>First Name:</label>
                    <input type="text" id="firstName" name="firstName" value={formData.firstName || ''} onChange={handleChange} style={inputStyle} />
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="lastName" style={labelStyle}>Last Name:</label>
                    <input type="text" id="lastName" name="lastName" value={formData.lastName || ''} onChange={handleChange} style={inputStyle} />
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="phone" style={labelStyle}>Phone Number:</label>
                    <PhoneInput
                        country={'in'}
                        value={formData.phone}
                        onChange={(value) => setFormData(prev => ({ ...prev, phone: value }))}
                        enableSearch={true}
                        inputStyle={inputStyle} // Full width
                    />
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="email" style={labelStyle}>Email:</label>
                    <input type="text" name="email" value={formData.email || ''} onChange={handleChange} style={inputStyle} />
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="gender" style={labelStyle}>Gender:</label>
                    <select
                        id="gender"
                        name="gender"
                        value={formData.gender || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">-- Select Gender --</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                        <option value="Other">Other</option>
                    </select>
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="mode" style={labelStyle}>Mode:</label>
                    <select
                        id="mode"
                        name="mode"
                        value={formData.mode || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">-- Select Batch --</option>
                        <option value="morning">Morning</option>
                        <option value="evening">Evening</option>
                    </select>
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="areYouA" style={labelStyle}>Are You a:</label>
                    <select
                        id="areYouA"
                        name="areYouA"
                        value={formData.areYouA || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">-- Select Course --</option>
                        <option value="student">Student</option>
                        <option value="workprofession">Work Profession</option>
                        <option value="entrepreneur">Entrepreneur</option>
                        <option value="freelancer">Freelancer</option>
                        <option value="other">Other</option>
                    </select>
                </div>
                
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="aitool" style={labelStyle}>Have You Used AI Tools:</label>
                    <select
                        id="aitool"
                        name="aitool"
                        value={formData.aitool || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">-- Select Mode --</option>
                        <option value="yes">Yes</option>
                        <option value="no">No</option>
                        <option value="Heard">Heard Them But Not Used</option>
                    </select>
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="current" style={labelStyle}>Your Current Field / Industry:</label>
                    <input type="text" id="current" name="current" value={formData.current || ''} onChange={handleChange} style={inputStyle} />
                </div>  
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="chooseworkshop" style={labelStyle}>Why Choosing This Workshop?:</label>
                    <input type="text" id="chooseworkshop" name="chooseworkshop" value={formData.chooseworkshop || ''} onChange={handleChange} style={inputStyle} />
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="aboutas" style={labelStyle}>How Did You Hear About Us? :</label>
                    <select
                        id="aboutas"
                        name="aboutas"
                        value={formData.aboutas || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">Select</option>
                        <option value="facebook/instagram">facebook/instagram</option>
                        <option value="googlesearch">googlesearch</option>
                        <option value="newspaper">newspaper</option>
                        <option value="family">family</option>
                        <option value="akasastudent">Akasa Student</option>
                        <option value="other">Other</option>
                    </select>
                </div>
                <div className="form-group" style={formGroupStyle}>
                    <label htmlFor="preferredBatch" style={labelStyle}>Preferred Batch:</label>
                    <select
                        id="preferredBatch"
                        name="preferredBatch"
                        value={formData.preferredBatch || ''}
                        onChange={handleChange}
                        style={inputStyle}
                    >
                        <option value="">Select</option>
                        <option value="june">June 13, 2025 - Offline only at Akasa Institute EkKatuthangal Chennai</option>
                        <option value="june28">June 28, 2025 - Online & Offline option available</option>
                    </select>
                </div>
                <button type="submit" className="submit-button" style={submitButtonStyle}>Update Application</button>
            </form>
        </div>
    );
};

export default Edit;
