import React, { useRef } from 'react';
import html2pdf from 'html2pdf.js';

function App() {
  const formRef = useRef();

  const headerStyle = {
    backgroundColor: '#f0f0f0',
    padding: '20px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  };

  const titleStyle = {
    margin: 0,
    fontSize: '24px',
    textAlign: 'center',
  };

  const logoStyle = {
    height: '60px',
    marginRight: '20px',
  };

  const formWrapperStyle = {
    display: 'flex',
    justifyContent: 'center',
    padding: '40px 20px',
    backgroundColor: '#f9f9f9',
  };

  const formContainerStyle = {
    width: '100%',
    maxWidth: '600px',
    padding: '30px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    fontFamily: 'Arial, sans-serif',
  };

  const formRowStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  };

  const labelStyle = {
    width: '150px',
    fontWeight: 'bold',
  };

  const inputStyle = {
    flex: 1,
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  };

  const submitStyle = {
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '4px',
    marginTop: '20px',
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const opt = {
      margin: 0.5,
      filename: 'akasa_receipt.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: 'in', format: 'letter', orientation: 'portrait' }
    };
    html2pdf().set(opt).from(formRef.current).save();
  };

  return (
    <div ref={formRef}>
      {/* Header */}
      <div style={headerStyle}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src="https://akasainstitute.com/wp-content/uploads/2025/05/WhatsApp-Image-2025-04-25-at-9.31.43-PM.jpeg"
            alt="Logo"
            style={logoStyle}
          />
          <div>
            <h1 style={titleStyle}>Akasa Institute</h1>
            <p style={{ margin: 0 }}>(A unit of Akasa Unitech PvtLtd)</p>
          </div>
        </div>
      </div>

      {/* Form Wrapper */}
      <div style={formWrapperStyle}>
        <form style={formContainerStyle} onSubmit={handleSubmit}>
          <div style={formRowStyle}>
            <label style={labelStyle}>Register No:</label>
            <input type="number" name="regno" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Date:</label>
            <input type="date" name="date" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Received from:</label>
            <input type="text" name="receivedFrom" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Contact Us:</label>
            <input type="tel" name="phone" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Email Id:</label>
            <input type="email" name="email" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Payment Details:</label>
            <input type="text" name="paymentDetails" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Purpose:</label>
            <input type="text" name="purpose" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Amount:</label>
            <input type="number" name="amount" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Payment Mode:</label>
            <select name="mode" style={inputStyle}>
              <option value="">---Select---</option>
              <option value="upi">UPI</option>
              <option value="cash">Cash</option>
              <option value="bank">Bank Transaction</option>
            </select>
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}>Transaction ID:</label>
            <input type="text" name="transactionId" style={inputStyle} />
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}></label>
            <label>
              <input type="checkbox" name="agree" required /> I confirm the information is correct.
            </label>
          </div>

          <div style={formRowStyle}>
            <label style={labelStyle}></label>
            <button type="submit" style={submitStyle}>Submit & Download PDF</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
