import React from 'react';

const ViewApplication = ({ application, onClose }) => {
  const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  };

  const containerStyle = {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    width: '80%',
    maxWidth: '800px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    overflowY: 'auto',
    maxHeight: '90vh',
  };

  const headingStyle = {
    fontSize: '24px',
    marginBottom: '15px',
    color: '#333',
    textAlign: 'center',
  };

  const detailsStyle = {
    marginBottom: '20px',
  };

  const paragraphStyle = {
    fontSize: '16px',
    lineHeight: '1.6',
    color: '#555',
    marginBottom: '8px',
  };

  const closeButtonStyle = {
    backgroundColor: '#4CAF50',
    color: 'white',
    padding: '10px 15px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
    display: 'block',
    margin: '0 auto',
  };

  return (
    <div className="view-application-overlay" style={overlayStyle}>
      <div className="view-application-container" style={containerStyle}>
        <h3 style={headingStyle}>Application Details</h3>
        <div className="application-details" style={detailsStyle}>
          <p style={paragraphStyle}><strong>First Name:</strong> {application.firstName}</p>
          <p style={paragraphStyle}><strong>Last Name:</strong> {application.lastName}</p>
          <p style={paragraphStyle}><strong>Phone Number:</strong> {application.phone}</p>
          <p style={paragraphStyle}><strong>Email:</strong> {application.email}</p>
          <p style={paragraphStyle}><strong>Gender:</strong> {application.gender}</p>
          <p style={paragraphStyle}><strong>Mode:</strong> {application.mode}</p>
          <p style={paragraphStyle}><strong>Are You A:</strong> {application.areYouA}</p>
          <p style={paragraphStyle}><strong>Have You Used AI Tools:</strong> {application.aitool}</p>
          <p style={paragraphStyle}><strong>Your Current Field / Industry:</strong> {application.current}</p>
          <p style={paragraphStyle}><strong>Why Choosing This Workshop?:</strong> {application.chooseworkshop}</p>
          <p style={paragraphStyle}><strong>How Did You Hear About Us?:</strong> {application.aboutas}</p>
          <p style={paragraphStyle}><strong>Preferred Batch:</strong> {application.preferredBatch}</p>
        </div>
        <button onClick={onClose} className="close-button" style={closeButtonStyle}>Close</button>
      </div>
    </div>
  );
};

export default ViewApplication;
