import React, { useRef, useState } from 'react';
import html2pdf from 'html2pdf.js';
import stamp from './stamp.png';
import { useNavigate } from 'react-router-dom';

function App() {
  const formRef = useRef();
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    regno: '',
    date: '',
    receivedFrom: '',
    phone: '',
    email: '',
    paymentDetails: '',
    purpose: '',
    amount: '',
    mode: '',
    transactionId: '',
    agree: false
  });

  const [downloading, setDownloading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const loadImagesThenDownload = () => {
    const images = formRef.current.querySelectorAll("img");
    const promises = Array.from(images).map(img => {
      if (img.complete) return Promise.resolve();
      return new Promise(resolve => {
        img.onload = img.onerror = resolve;
      });
    });
    return Promise.all(promises);
  };

  const goToAdmin = () => {
    navigate('/AdminPanel');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setDownloading(true);

    setTimeout(async () => {
      await loadImagesThenDownload();

      const opt = {
        margin: 0.5,
        filename: 'akasa_receipt.pdf',
        image: { type: 'jpeg', quality: 0.98 },
        html2canvas: { scale: 2 },
        jsPDF: { unit: 'in', format: 'letter', orientation: 'portrait' }
      };

      await html2pdf().set(opt).from(formRef.current).save();

      setDownloading(false);
    }, 300);
  };

  const styles = {
    header: {
      backgroundColor: '#f0f0f0',
      padding: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    adminStyle: {
      padding: '10px 16px',
      backgroundColor: '#007bff',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: '14px',
    },
    title: {
      margin: 0,
      fontSize: '24px',
      textAlign: 'center',
    },
    logo: {
      height: '60px',
      marginRight: '20px',
    },
    formWrapper: {
      display: 'flex',
      justifyContent: 'center',
      padding: '40px 20px',
      backgroundColor: downloading ? 'transparent' : '#f9f9f9',
    },
    formContainer: {
      width: '100%',
      maxWidth: '600px',
      padding: '30px',
      border: downloading ? 'none' : '1px solid #ddd',
      borderRadius: downloading ? '0' : '8px',
      backgroundColor: downloading ? 'transparent' : '#fff',
      boxShadow: downloading ? 'none' : '0 4px 8px rgba(0, 0, 0, 0.1)',
      fontFamily: 'Arial, sans-serif',
      position: 'relative',
    },
    formRow: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '15px',
    },
    label: {
      width: '150px',
      fontWeight: 'bold',
    },
    input: {
      flex: 1,
      padding: '8px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
    submit: {
      padding: '10px 20px',
      backgroundColor: 'green',
      color: 'white',
      border: 'none',
      cursor: 'pointer',
      borderRadius: '4px',
      marginTop: '20px',
    },
    stampImage: {
      width: '120px',
      display: 'block',
      margin: '20px auto',
      opacity: 0.9,
    }
  };

  return (
    <>
      {/* Header */}
      <div style={styles.header}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src="https://akasainstitute.com/wp-content/uploads/2025/05/WhatsApp-Image-2025-04-25-at-9.31.43-PM.jpeg"
            alt="Logo"
            style={styles.logo}
          />
          <div>
            <h1 style={styles.title}>Akasa Institute</h1>
            <p style={{ margin: 0 }}>(A unit of Akasa Unitech PvtLtd)</p>
          </div>
        </div>
        <button style={styles.adminStyle} onClick={goToAdmin}>Admin</button>
      </div>

      {/* Form */}
      <div style={styles.formWrapper}>
        <form style={styles.formContainer} onSubmit={handleSubmit} ref={formRef}>
          {[
            ['Register No:', 'regno', 'number'],
            ['Date:', 'date', 'date'],
            ['Received from:', 'receivedFrom', 'text'],
            ['Contact Us:', 'phone', 'tel'],
            ['Email Id:', 'email', 'email'],
            ['Payment Details:', 'paymentDetails', 'text'],
            ['Purpose:', 'purpose', 'text'],
            ['Amount:', 'amount', 'number'],
            ['Transaction ID:', 'transactionId', 'text'],
          ].map(([label, name, type]) => (
            <div style={styles.formRow} key={name}>
              <label style={styles.label}>{label}</label>
              {downloading ? (
                <div>{formData[name]}</div>
              ) : (
                <input
                  type={type}
                  name={name}
                  value={formData[name]}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              )}
            </div>
          ))}

          {/* Payment Mode */}
          <div style={styles.formRow}>
            <label style={styles.label}>Payment Mode:</label>
            {downloading ? (
              <div>{formData.mode}</div>
            ) : (
              <select
                name="mode"
                value={formData.mode}
                onChange={handleChange}
                style={styles.input}
                required
              >
                <option value="">---Select---</option>
                <option value="upi">UPI</option>
                <option value="cash">Cash</option>
                <option value="bank">Bank Transaction</option>
              </select>
            )}
          </div>

          {/* Agreement */}
          {!downloading && (
            <div style={styles.formRow}>
              <label style={styles.label}></label>
              <label>
                <input
                  type="checkbox"
                  name="agree"
                  checked={formData.agree}
                  onChange={handleChange}
                  required
                />{' '}
                I confirm the information is correct.
              </label>
            </div>
          )}

          {/* Stamp and Signature */}
          <div style={{ position: 'relative', marginTop: '40px' }}>
            <img
              src={stamp}
              alt="Stamp"
              style={{
                position: 'absolute',
                top: '0',
                zIndex: 0,
                width: '150px',
              }}
            />
            <div style={{ position: 'relative', zIndex: 1 }}>
              <h5>Authorized Signature</h5>
              <h6>Akasa Institute (A Unit of Akasa Unitech Pvt Ltd)</h6>
            </div>
          </div>

          {/* Submit Button */}
          {!downloading && (
            <div style={styles.formRow}>
              <label style={styles.label}></label>
              <button type="submit" style={styles.submit}>
                Submit & Download PDF
              </button>
            </div>
          )}
        </form>
      </div>
    </>
  );
}

export default App;
