// AdminPanel.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ViewApplication from './ViewApplication';

const AdminPanel = () => {
  const [applications, setApplications] = useState([]);
  const [message, setMessage] = useState('');
  const [selectedApplication, setSelectedApplication] = useState(null);
  const API_ENDPOINT = 'http://localhost:5000/workshop';

  const fetchApplications = async () => {
    try {
      const response = await fetch(API_ENDPOINT);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setApplications(data);
    } catch (error) {
      console.error('Error fetching applications:', error);
      setMessage('Error fetching applications');
    }
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this application?')) {
      try {
        const response = await fetch(`${API_ENDPOINT}/${id}`, {
          method: 'DELETE',
        });
        const data = await response.json();
        if (response.ok) {
          setMessage(data.message);
          setApplications(applications.filter(application => application.id !== id));
        } else {
          setMessage('Error deleting application');
        }
      } catch (error) {
        console.error('Error deleting application:', error);
        setMessage('Error deleting application');
      }
    }
  };

  const handleView = (application) => {
    setSelectedApplication(application);
  };

  const handleCloseView = () => {
    setSelectedApplication(null);
  };

  const dashboardStyle = {
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
    backgroundColor: '#f4f4f4',
    minHeight: '100vh',
  };

  const headingStyle = {
    color: '#333',
    textAlign: 'center',
    marginBottom: '20px',
  };

  const messageStyle = {
    backgroundColor: '#d4edda',
    color: '#155724',
    padding: '10px',
    marginBottom: '20px',
    border: '1px solid #c3e6cb',
    borderRadius: '4px',
    textAlign: 'center',
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    backgroundColor: '#fff',
  };

  const tableHeaderStyle = {
    backgroundColor: '#343a40',
    color: '#fff',
    padding: '12px',
    textAlign: 'left',
    borderBottom: '2px solid #dee2e6',
  };

  const tableCellStyle = {
    padding: '12px',
    borderBottom: '1px solid #dee2e6',
  };

  const actionButtonStyle = {
    padding: '8px 12px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '14px',
    textDecoration: 'none',
    color: '#fff',
  };

  const viewButtonStyle = {
    ...actionButtonStyle,
    backgroundColor: '#007bff',
  };

  const editButtonStyle = {
    ...actionButtonStyle,
    backgroundColor: '#28a745',
  };

  const deleteButtonStyle = {
    ...actionButtonStyle,
    backgroundColor: '#dc3545',
  };
  const logoStyle = {
  position: 'absolute',
  right: '20px',
  width: '70px',
  height: '70px',
  objectFit: 'cover', // changed to 'cover' for better circle crop
  borderRadius: '50%', // makes the image round
  overflow: 'hidden',  // ensures nothing spills out
  d: '-10px',
};  
  return (
  <div className="admin-dashboard" style={dashboardStyle}>
    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
      <h2 style={headingStyle}>Admin Dashboard</h2>
      <img
        src="https://static.vecteezy.com/system/resources/previews/006/801/208/non_2x/trash-can-icon-free-vector.jpg"
        alt="trash"
        style={logoStyle}
      />
    </div>
      {message && <p className="message" style={messageStyle}>{message}</p>}
      <table className="application-table" style={tableStyle}>
        <thead>
          <tr>
            <th style={tableHeaderStyle}>Name</th>
            <th style={tableHeaderStyle}>Phone</th>
            <th style={tableHeaderStyle}>Email</th>
            <th style={tableHeaderStyle}>Gender</th>
            <th style={tableHeaderStyle}>Mode</th>
            <th style={tableHeaderStyle}>Are You A</th>
            <th style={tableHeaderStyle}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {applications.map(application => (
            <tr key={application.id}>
              <td style={tableCellStyle}>{application.firstName} {application.lastName}</td>
              <td style={tableCellStyle}>{application.phone}</td>
              <td style={tableCellStyle}>{application.email}</td>
              <td style={tableCellStyle}>{application.gender}</td>
              <td style={tableCellStyle}>{application.mode}</td>
              <td style={tableCellStyle}>{application.areYouA}</td>
              <td style={tableCellStyle}>
                <button onClick={() => handleView(application)} className="view-button" style={viewButtonStyle}>View</button>
                <Link to={`/edit/${application.id}`} className="edit-button" style={editButtonStyle}>Edit</Link>
                <button onClick={() => handleDelete(application.id)} className="delete-button" style={deleteButtonStyle}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedApplication && (
        <ViewApplication application={selectedApplication} onClose={handleCloseView} />
      )}
    </div>
  );
};

export default AdminPanel;
